package java001_basic;

public class Java009_operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		  int x = 3;
		  //전위 연산자
		  ++x ; // x=x+1,x+=1
		  System.out.println(x);
		  
		  --x;
		  System.out.println(x);
		 
		  
		  int y=5;
		  //후위연산자
		  y++;
		  System.out.println(y);
		  
		  y--;
		  System.out.println(y);
	
	

	}

}
