package java002_statements;

public class Java023_switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
String color = "red";

switch(color) {
case "red" : System.out.println("#ff0000"); break;
case "green" : System.out.println("#ff0070"); break;
case "blue" : System.out.println("#ff0060"); break;
case "white" : System.out.println("#ff0004"); break;
case "black" : System.out.println("#ff0002"); break;

}
	}

}
